import axios from 'axios';
import { useFormik } from 'formik'
import React from 'react'
import { useState } from 'react';
import * as Yup from 'yup';


export default function SginUp() {

  const [errorMsg, setErrorMsg]=useState('')
  const [loading, setLoading]=useState(true)
  function sendDataToApi(values){
    setLoading(false)
    axios.post('https://ecommerce.routemisr.com/api/v1/auth/signup', values).then(({data})=>{
      console.log(data);
    }).catch((err)=>{
       setErrorMsg(err.response.data.message)
       setLoading(true)
    })
    
  }

  function validationSchema(values){

    let schema =new Yup.object({
      name:Yup.string().min(2).max(20).required(),
      email:Yup.string().email().required(),
      password:Yup.string().matches(/^(?=.*[a-z])(?=.*[A-Z])(?=.*\d)(?=.*[@$!%*?&])[A-Za-z\d@$!%*?&]{8,}$/).required(),
      rePassword:Yup.string().oneOf([Yup.ref('password')]).required(),
    })
    return schema
  }

  let register = useFormik({
    initialValues:{
      name:'',
      email:'',
      password:'',
      rePassword:''

    },
    validationSchema,
    onSubmit: (values)=>{
      console.log(values);
      sendDataToApi(values)
    }
  })
  return (
    <div>
      <div className="w-75 m-auto my-5">
        <h2>Register Now:</h2>
        <form className='mt-4' onSubmit={register.handleSubmit}>
           <label htmlFor="name">Name:</label>
           <input value={register.values.name} onBlur={register.handleBlur} onChange={register.handleChange} type="text" name='name' className='form-control mb-3' id='name' />
           {register.errors.name && register.touched.name? <div className="alert alert-danger">{register.errors.name}</div>:''}

           <label htmlFor="email">Email:</label>
           <input value={register.values.email} onBlur={register.handleBlur} onChange={register.handleChange} type="email" name='email' className='form-control mb-3' id='email' />
           {register.errors.email && register.touched.email? <div className="alert alert-danger">{register.errors.email}</div>:''}

           <label htmlFor="password">Password:</label>
           <input value={register.values.password} onBlur={register.handleBlur} onChange={register.handleChange} type="password" name='password'  className='form-control mb-3' id='password' />
           {register.errors.password && register.touched.password? <div className="alert alert-danger">{register.errors.password}</div>:''}

           <label htmlFor="rePassword">rePassword:</label>
           <input value={register.values.rePassword} onBlur={register.handleBlur} onChange={register.handleChange} type="password" name='rePassword'  className='form-control mb-3' id='rePassword' />
           {register.errors.rePassword && register.touched.rePassword? <div className="alert alert-danger">{register.errors.rePassword}</div>:''}

           {errorMsg? <div className="alert alert-danger">{errorMsg}</div>: ''}
            <button disabled={!(register.dirty && register.isValid)} type='submit' className='btn bg-main text-white'>
                {loading}
            </button>
        </form>
      </div>
    </div>
  )
}
