import { useFormik } from 'formik'
import React from 'react'
import * as Yup from 'yup';


export default function SginUp() {

  function validationSchema(values){

    let schema =new Yup.object({
      name:Yup.string().min(2).max(20).required(),
      email:Yup.string().email().required(),
      password:Yup.string().matches(/^(?=.*[a-z])(?=.*[A-Z])(?=.*\d)(?=.*[@$!%*?&])[A-Za-z\d@$!%*?&]{8,}$/).required(),
      rePassword:Yup.string().oneOf([Yup.ref('password')]).required(),
    })

    return sc
  }

  let register = useFormik({
    initialValues:{
      name:'',
      email:'',
      password:'',
      rePassword:''

    },
    validationSchema,
    onSubmit: (values)=>{
      console.log(values);
    }
  })
  return (
    <div>
      <div className="w-75 m-auto my-5">
        <h2>Register Now:</h2>
        <form onSubmit={register.handleSubmit}>
           <label htmlFor="name">Name:</label>
           <input value={register.values.name} onChange={register.handleChange} type="text" name='name' className='form-control mb-3' id='name' />
           <div className="alert alert-danger"></div>

           <label htmlFor="email">Email:</label>
           <input value={register.values.email} onChange={register.handleChange} type="email" name='email' className='form-control mb-3' id='email' />

           <label htmlFor="password">Password:</label>
           <input value={register.values.password} onChange={register.handleChange} type="password" name='password'  className='form-control mb-3' id='password' />

           <label htmlFor="rePassword">rePassword:</label>
           <input value={register.values.rePassword} onChange={register.handleChange} type="password" name='rePassword'  className='form-control mb-3' id='rePassword' />

            <button type='submit' className='btn bg-main text-white'>SginUp</button>
        </form>
      </div>
    </div>
  )
}
